import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';
import Explore from './components/Explore';
import Messages from './components/Messages';

function App() {
  const [view, setView] = useState('welcome');
  const [selectedReceiver, setSelectedReceiver] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setView('dashboard');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setView('welcome');
  };

  return (
    <div className="App">
      <h1>SkillSwap</h1>
      {view === 'welcome' && (
        <div className="page fade-in" style={{ justifyContent: 'center', minHeight: '80vh', textAlign: 'center' }}>
          <div>
            <h1 style={{ fontSize: '4rem', marginBottom: '20px', color: '#f5f5f5' }}>SkillSwap</h1>
            <p style={{ fontSize: '1.2rem', marginBottom: '40px', maxWidth: '600px', margin: '0 auto 40px', color: '#ccc' }}>A platform for exchanging skills and connecting with peers.</p>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
              <button onClick={() => setView('login')}>Login</button>
              <button onClick={() => setView('register')}>Register</button>
            </div>
          </div>
        </div>
      )}
      {view === 'login' && (
        <div>
          <button onClick={() => setView('welcome')} style={{ marginBottom: '20px' }}>Back</button>
          <Login onSuccess={() => setView('dashboard')} />
        </div>
      )}
      {view === 'register' && (
        <div>
          <button onClick={() => setView('welcome')} style={{ marginBottom: '20px' }}>Back</button>
          <Signup />
        </div>
      )}
      {view === 'dashboard' && (
        <div>
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>Logout</button>
          <Dashboard setView={setView} />
        </div>
      )}
      {view === 'explore' && (
        <div>
          <button onClick={() => setView('dashboard')} style={{ position: 'absolute', top: '20px', right: '20px' }}>Back to Dashboard</button>
          <Explore setView={setView} setSelectedReceiver={setSelectedReceiver} />
        </div>
      )}
      {view === 'messages' && (
        <div>
          <Messages setView={setView} selectedReceiver={selectedReceiver} />
        </div>
      )}
    </div>
  );
}

export default App;
