import React, { useState, useEffect, useRef, useCallback } from 'react';
import API_BASE from '../api';

const Messages = ({ setView, selectedReceiver }) => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const fetchMessages = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token || !selectedReceiver?.id) return;
    try {
      const response = await fetch(`${API_BASE}/messages?with=${selectedReceiver.id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        const data = await response.json();
        setMessages(Array.isArray(data) ? data : []);
      } else {
        setMessages([]);
      }
    } catch (error) {
      console.error('Error fetching messages:', error);
      setMessages([]);
    }
  }, [selectedReceiver?.id]);

  useEffect(() => {
    if (selectedReceiver?.id) {
      fetchMessages();
      const interval = setInterval(fetchMessages, 5000); // Refresh every 5 seconds
      return () => clearInterval(interval);
    }
  }, [selectedReceiver?.id, fetchMessages]);

  const handleSend = async () => {
    if (!message.trim() || !selectedReceiver) return;
    const token = localStorage.getItem('token');
    try {
      await fetch(`${API_BASE}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ receiverId: selectedReceiver.id, message })
      });
      setMessage('');
      fetchMessages(); // Refresh messages
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  if (!selectedReceiver?.id) {
    return (
      <div className="page fade-in">
        <h2>My Messages</h2>
        <p>Select a user to start chatting.</p>
        <button onClick={() => setView('explore')}>Back to Explore</button>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', backgroundColor: '#e5ddd5' }}>
      <div style={{ backgroundColor: '#075e54', color: 'white', padding: '10px', textAlign: 'center' }}>
        <h3>{selectedReceiver?.name || 'Chat'}</h3>
        <button onClick={() => setView('explore')} style={{ position: 'absolute', left: '10px', top: '10px', background: 'none', border: 'none', color: 'white', fontSize: '20px' }}>‚Üê</button>
      </div>
      <div style={{ flex: 1, overflowY: 'auto', padding: '10px' }}>
        {messages.map((msg) => {
          const isSent = msg.senderId._id === localStorage.getItem('userId');
          return (
            <div key={msg._id} style={{ display: 'flex', justifyContent: isSent ? 'flex-end' : 'flex-start', marginBottom: '10px' }}>
              <div style={{
                maxWidth: '70%',
                padding: '10px',
                borderRadius: '10px',
                backgroundColor: isSent ? '#dcf8c6' : 'white',
                color: '#333',
                wordWrap: 'break-word'
              }}>
                <p style={{ margin: 0 }}>{msg.message}</p>
                <small style={{ color: '#999', fontSize: '12px' }}>{new Date(msg.timestamp).toLocaleTimeString()}</small>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>
      <div style={{ display: 'flex', padding: '10px', backgroundColor: 'white', borderTop: '1px solid #ccc' }}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message..."
          style={{ flex: 1, padding: '10px', borderRadius: '20px', border: '1px solid #ccc', marginRight: '10px' }}
        />
        <button onClick={handleSend} style={{ padding: '10px 20px', borderRadius: '20px', backgroundColor: '#25d366', color: 'white', border: 'none' }}>Send</button>
      </div>
    </div>
  );
};

export default Messages;
