import React, { useState } from 'react';
import API_BASE from '../api';

const Dashboard = ({ setView }) => {
  const [skillsOffer, setSkillsOffer] = useState('');
  const [interests, setInterests] = useState('');
  const [skillsLearn, setSkillsLearn] = useState('');
  const [message, setMessage] = useState('');
  const handleDeleteProfile = async () => {
    if (!window.confirm('Are you sure you want to delete your profile? This action cannot be undone.')) return;
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`${API_BASE}/profile`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (response.ok) {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        window.location.reload(); // Or setView to welcome
      } else {
        setMessage('Error deleting profile');
      }
    } catch (error) {
      setMessage('Network error');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('Not logged in');
      return;
    }
    try {
      const response = await fetch(`${API_BASE}/profile`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          skillsOffer: skillsOffer.split(',').map(s => s.trim()),
          interests: interests.split(',').map(s => s.trim()),
          skillsLearn: skillsLearn.split(',').map(s => s.trim())
        }),
      });
      const data = await response.json();
      if (response.ok) {
        setMessage('Profile created successfully!');
      } else {
        setMessage(data.message || 'Error creating profile');
      }
    } catch (error) {
      setMessage('Network error');
    }
  };

  return (
    <div className="page fade-in">
      <div className="profile-card" style={{ textAlign: 'center', marginBottom: '30px', background: 'linear-gradient(135deg, rgba(255,255,255,0.9), rgba(255,255,255,0.95))' }}>
        <h2 style={{ background: 'linear-gradient(135deg, #667eea, #764ba2)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>Welcome to SkillSwap</h2>
        <p>Connect with peers, share skills, and learn together.</p>
      </div>
      <div className="profile-card">
        <h3>Complete Your Profile</h3>
        <form onSubmit={handleSubmit} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
          <div style={{ gridColumn: '1 / -1' }}>
            <input
              type="text"
              placeholder="Skills I can offer (comma separated)"
              value={skillsOffer}
              onChange={(e) => setSkillsOffer(e.target.value)}
              required
            />
          </div>
          <input
            type="text"
            placeholder="Interests (comma separated)"
            value={interests}
            onChange={(e) => setInterests(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Skills I want to learn (comma separated)"
            value={skillsLearn}
            onChange={(e) => setSkillsLearn(e.target.value)}
            required
          />
          <div style={{ gridColumn: '1 / -1', textAlign: 'center' }}>
            <button type="submit">Save Profile</button>
          </div>
        </form>
        {message && <p style={{ color: message.includes('success') ? 'green' : 'red', textAlign: 'center' }}>{message}</p>}
      </div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '30px', width: '100%' }}>
        <div className="profile-card" style={{ textAlign: 'center', cursor: 'pointer', background: 'linear-gradient(135deg, rgba(102,126,234,0.1), rgba(118,75,162,0.1))' }} onClick={() => setView('explore')}>
          <div style={{ fontSize: '4rem', marginBottom: '15px' }}>üîç</div>
          <h3>Explore Profiles</h3>
          <p>Find people to connect with</p>
        </div>
        <div className="profile-card" style={{ textAlign: 'center', cursor: 'pointer', background: 'linear-gradient(135deg, rgba(118,75,162,0.1), rgba(240,147,251,0.1))' }} onClick={() => setView('messages')}>
          <div style={{ fontSize: '4rem', marginBottom: '15px' }}>üí¨</div>
          <h3>My Messages</h3>
          <p>Chat with your connections</p>
        </div>
        <div className="profile-card" style={{ textAlign: 'center', cursor: 'pointer', background: 'linear-gradient(135deg, rgba(255,0,0,0.1), rgba(255,100,100,0.1))' }} onClick={handleDeleteProfile}>
          <div style={{ fontSize: '4rem', marginBottom: '15px' }}>üóëÔ∏è</div>
          <h3>Delete Profile</h3>
          <p>Remove your account</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
