import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';
import Explore from './components/Explore';
import Messages from './components/Messages';

function App() {
  const [view, setView] = useState('welcome');
  const [selectedReceiver, setSelectedReceiver] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setView('dashboard');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setView('welcome');
  };

  return (
    <div className="App">
      {view === 'welcome' && (
        <article style={{ maxWidth: '800px', margin: '0 auto' }} className="fade-in">
          <h1>SkillSwap</h1>
          <h2>Welcome to SkillSwap</h2>
          <p>Connect with peers, share skills, and learn together.</p>
          <div className="auth-buttons">
            <button onClick={() => setView('login')}>Login</button>
            <button onClick={() => setView('register')}>Register</button>
          </div>
        </article>
      )}
      {view === 'login' && (
        <div>
          <button onClick={() => setView('welcome')}>Back</button>
          <Login onSuccess={() => setView('dashboard')} />
        </div>
      )}
      {view === 'register' && (
        <div>
          <button onClick={() => setView('welcome')}>Back</button>
          <Signup />
        </div>
      )}
      {view === 'dashboard' && (
        <div>
          <button onClick={handleLogout}>Logout</button>
          <Dashboard setView={setView} />
        </div>
      )}
      {view === 'explore' && (
        <div>
          <button onClick={() => setView('dashboard')}>Back to Dashboard</button>
          <Explore setView={setView} setSelectedReceiver={setSelectedReceiver} />
        </div>
      )}
      {view === 'messages' && (
        <div>
          <button onClick={() => setView('dashboard')}>Back to Dashboard</button>
          <Messages setView={setView} selectedReceiver={selectedReceiver} />
        </div>
      )}
    </div>
  );
}

export default App;
