import React, { useState, useEffect } from 'react';
import API_BASE from '../api';

const Explore = ({ setView, setSelectedReceiver }) => {
  const [profiles, setProfiles] = useState([]);
  const [searchSkill, setSearchSkill] = useState('');
  const [message, setMessage] = useState('');

  const fetchProfiles = async (skill = '') => {
    try {
      const url = skill ? `${API_BASE}/profiles/search?skill=${encodeURIComponent(skill)}` : `${API_BASE}/profiles`;
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        const currentUserId = localStorage.getItem('userId');
        const filtered = data.filter(profile => profile.userId._id !== currentUserId);
        setProfiles(filtered);
      } else {
        setMessage('Error fetching profiles');
      }
    } catch (error) {
      setMessage('Network error');
    }
  };

  useEffect(() => {
    fetchProfiles();
  }, []);

  const handleSearch = () => {
    fetchProfiles(searchSkill);
  };

  return (
    <div className="fade-in">
      <div className="page">
        <div className="profile-card" style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h2>üîç Explore Profiles</h2>
          <p>Discover new skills and connect with amazing people!</p>
        </div>
        <div className="profile-card">
          <h3>Search by Skills</h3>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            <input
              type="text"
              placeholder="Search by skills"
              value={searchSkill}
              onChange={(e) => setSearchSkill(e.target.value)}
            />
            <button onClick={handleSearch}>üîç Search</button>
            <button onClick={() => { setSearchSkill(''); fetchProfiles(); }}>üåü Show All</button>
          </div>
          {message && <p style={{ color: 'red' }}>{message}</p>}
        </div>
      </div>
      <div className="profiles-container">
        {profiles.map((profile) => (
          <div key={profile._id} className="profile-card">
            <h3>üë§ {profile.userId.name}</h3>
            <p>üìß Email: {profile.userId.email}</p>
            <p>üéØ Skills I can offer: {profile.skillsOffer.join(', ')}</p>
            <p>‚ù§Ô∏è Interests: {profile.interests.join(', ')}</p>
            <p>üìö Skills I want to learn: {profile.skillsLearn.join(', ')}</p>
            <button onClick={() => { setSelectedReceiver({ id: profile.userId._id, name: profile.userId.name }); setView('messages'); }}>üí¨ Message</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Explore;
