import React, { useState, useEffect } from 'react';
import API_BASE from '../api';

const MyProfile = ({ setView }) => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMyProfile();
  }, []);

  const fetchMyProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE}/profile/me`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      if (response.ok) {
        const data = await response.json();
        setProfile(data);
      } else if (response.status === 404) {
        setError('Profile not found. Please create your profile first.');
      } else {
        setError('Failed to load profile');
      }
    } catch (error) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ 
        maxWidth: '800px', 
        margin: '0 auto', 
        padding: '40px 20px',
        textAlign: 'center',
        color: '#f5f5f5'
      }}>
        <h1>My Profile</h1>
        <p>Loading your profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        maxWidth: '800px', 
        margin: '0 auto', 
        padding: '40px 20px',
        textAlign: 'center',
        color: '#f5f5f5'
      }}>
        <h1>My Profile</h1>
        <p style={{ color: '#ffa8a8' }}>{error}</p>
        <button 
          onClick={() => setView('dashboard')}
          style={{
            background: 'linear-gradient(135deg, #9c27b0, #3f51b5)',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '4px',
            cursor: 'pointer',
            marginTop: '20px'
          }}
        >
          Back to Dashboard
        </button>
      </div>
    );
  }

  return (
    <div style={{ 
      maxWidth: '800px', 
      margin: '0 auto', 
      padding: '20px'
    }}>
      <div style={{ 
        background: 'linear-gradient(135deg, #2a2a2a, #1a1a1a)',
        border: '1px solid #444',
        borderRadius: '12px',
        padding: '30px',
        color: '#f5f5f5'
      }}>
        {/* Header */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '30px'
        }}>
          <h1 style={{ margin: 0 }}>My Profile</h1>
          <button 
            onClick={() => setView('dashboard')}
            style={{
              background: 'transparent',
              color: '#9c27b0',
              border: '1px solid #9c27b0',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            ‚Üê Back to Dashboard
          </button>
        </div>

        {/* Profile Data */}
        {profile && (
          <div>
            {/* Personal Information */}
            <div style={{ marginBottom: '30px' }}>
              <h2 style={{ color: '#9c27b0', marginBottom: '15px' }}>Personal Information</h2>
              <div style={{ 
                background: '#333',
                padding: '20px',
                borderRadius: '8px'
              }}>
                <div style={{ marginBottom: '10px' }}>
                  <strong style={{ color: '#ccc' }}>Name:</strong>
                  <span style={{ marginLeft: '10px', color: '#f5f5f5' }}>
                    {profile.name || 'Not specified'}
                  </span>
                </div>
                <div>
                  <strong style={{ color: '#ccc' }}>Email:</strong>
                  <span style={{ marginLeft: '10px', color: '#f5f5f5' }}>
                    {profile.userId?.email || 'Not available'}
                  </span>
                </div>
              </div>
            </div>

            {/* Skills I Can Offer */}
            <div style={{ marginBottom: '30px' }}>
              <h2 style={{ color: '#9c27b0', marginBottom: '15px' }}>Skills I Can Offer</h2>
              <div style={{ 
                background: '#333',
                padding: '20px',
                borderRadius: '8px',
                minHeight: '60px'
              }}>
                {profile.skillsOffer && profile.skillsOffer.length > 0 ? (
                  <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                    {profile.skillsOffer.map((skill, index) => (
                      <span 
                        key={index}
                        style={{
                          background: 'linear-gradient(135deg, #9c27b0, #3f51b5)',
                          color: 'white',
                          padding: '6px 12px',
                          borderRadius: '20px',
                          fontSize: '14px'
                        }}
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                ) : (
                  <p style={{ color: '#aaa', fontStyle: 'italic' }}>No skills added yet</p>
                )}
              </div>
            </div>

            {/* Interests */}
            <div style={{ marginBottom: '30px' }}>
              <h2 style={{ color: '#9c27b0', marginBottom: '15px' }}>My Interests</h2>
              <div style={{ 
                background: '#333',
                padding: '20px',
                borderRadius: '8px',
                minHeight: '60px'
              }}>
                {profile.interests && profile.interests.length > 0 ? (
                  <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                    {profile.interests.map((interest, index) => (
                      <span 
                        key={index}
                        style={{
                          background: 'linear-gradient(135deg, #ff9800, #ff5722)',
                          color: 'white',
                          padding: '6px 12px',
                          borderRadius: '20px',
                          fontSize: '14px'
                        }}
                      >
                        {interest}
                      </span>
                    ))}
                  </div>
                ) : (
                  <p style={{ color: '#aaa', fontStyle: 'italic' }}>No interests added yet</p>
                )}
              </div>
            </div>

            {/* Skills I Want to Learn */}
            <div style={{ marginBottom: '30px' }}>
              <h2 style={{ color: '#9c27b0', marginBottom: '15px' }}>Skills I Want to Learn</h2>
              <div style={{ 
                background: '#333',
                padding: '20px',
                borderRadius: '8px',
                minHeight: '60px'
              }}>
                {profile.skillsLearn && profile.skillsLearn.length > 0 ? (
                  <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                    {profile.skillsLearn.map((skill, index) => (
                      <span 
                        key={index}
                        style={{
                          background: 'linear-gradient(135deg, #4caf50, #2e7d32)',
                          color: 'white',
                          padding: '6px 12px',
                          borderRadius: '20px',
                          fontSize: '14px'
                        }}
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                ) : (
                  <p style={{ color: '#aaa', fontStyle: 'italic' }}>No skills to learn added yet</p>
                )}
              </div>
            </div>

            {/* Profile Created Info */}
            <div style={{ 
              marginTop: '40px',
              padding: '15px',
              background: 'rgba(156, 39, 176, 0.1)',
              border: '1px solid rgba(156, 39, 176, 0.3)',
              borderRadius: '8px',
              fontSize: '14px',
              color: '#ccc'
            }}>
              <p style={{ margin: 0 }}>
                Profile ID: {profile._id}
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MyProfile;
